import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as p,c as r,a as o}from"./app-e3a62ffd.js";const e={},a=o('<h1 id="大学英语四级-张智萍" tabindex="-1"><a class="header-anchor" href="#大学英语四级-张智萍" aria-hidden="true">#</a> 大学英语四级 张智萍</h1><p><strong>part 1</strong></p><p>作业比较多 上课一般 总体不推荐</p><p><strong>part 2</strong></p><p>别选，给分不好，而且老师硬给你灌输他有点异于常人的三观</p><p><strong>part 3</strong></p><p>给分还行</p><p><strong>part 4</strong></p><p>整理者备注：有原因不明的不推荐</p><p><strong>part 5</strong></p><p>课程介绍：学习外语教学与研究出版社的《新标准大学英语视听说教程4》。通过科学的设计、严谨的编写与丰富的资源有效促进学生英语综合应用能力、自主学习策略与综合文化素养的培养。上课主要锻炼演讲，阅读，写作能力。</p><p>讲课特色：按学号次序给学生排好座位，容易查处缺勤。上课讲英语视听说上的课文，做视听说书上的题。老师的课堂很有趣，总能在不同的故事中学到知识。</p><p>考核方式：按照期末考试和平时给分，比较严格。平时有作业，要做演讲展示。最后有期末考试。</p><p>（整理者备注，part5针对“大学英语五级”）</p>',14),n=[a];function s(_,c){return p(),r("div",null,n)}const h=t(e,[["render",s],["__file","大学英语四级 张智萍.html.vue"]]);export{h as default};
